mapper.select:
select count(*)
from cart
where user_id = #{user_id}

mapper.delete:
delete from cart
where user_id = #{user_id}
and cart_num = #{cart_num}

delete from cart
where user_id = #{user_id}
and cart_num in

	#{cartNum}

delete from cart
where user_id = #{orderDto.user_id}
and cart_num in

	#{cart}

mapper.update:
update cart
set order_de_amount = order_de_amount + #{order_de_amount}
where cart_num = #{cart_num}
and product_code = #{product_code}
